/** @jsx jsx */
import { jsx, Box, Container, Flex, Button } from 'theme-ui';
import { IoIosArrowRoundBack, IoIosArrowRoundForward } from 'react-icons/io';



export default function ButtonGroup({ next=null, previous=null }) {



  return (
      <Flex>

        {next && (<Button variant="primary">Next Milestone</Button>)}
        {previous && (<Button variant={next ? 'secondary' : 'primary'}>Previous Milestone</Button>)} 

      </Flex>
  );
}

const styles = {
  buttonGroup: {
    display: 'flex',
    justifyContent: 'center',
    mb: -4,
    button: {
      bg: 'transparent',
      border: '0px solid',
      fontSize: 40,
      cursor: 'pointer',
      px: '2px',
      color: '#BBC7D7',
      transition: 'all 0.25s',
      '&:hover': {
        color: 'text',
      },
      '&:focus': {
        outline: 0,
      },
    },
  },
};
